import React, {Fragment, useState} from 'react';
import { User } from 'react-feather';
import {Link} from "react-router-dom";


const LoginUserMenu = () => {
    const [showList, updateShowList] = useState(false);
    return (
        <Fragment>
            <li style={{listStyleType: "none"}} className="onhover-dropdown" onMouseEnter={() => updateShowList(true)} onMouseLeave={() => updateShowList(false)}>
                <div style={{ paddingLeft:'25px', paddingRight: '25px' }} className="media align-items-center">
                    <div className="f6 link dim ph3 pv2 mb2 dib white w-100"
                         style={{backgroundColor: '#4ac4f3', borderRadius: '30px'}}>
                    LOGIN
                    </div>
                </div>
                <ul className="profile-dropdown onhover-show-div p-20 profile-dropdown-hover">
                    <li><Link to="/userlogin"><User />User</Link></li>
                    <li><Link to="/neutrallogin"><User />Neutral</Link></li>
                </ul>
            </li>
        </Fragment>
    );
};


export default LoginUserMenu;
